import React, {useEffect,useState,Fragment} from 'react';

//importar cliente axios
import clienteaAxios from '../../config/axios';

import Cliente from './CLiente';

import { Link } from 'react-router-dom';

import { CRMContext } from '../../context/CRMContext';

function Clientes(){
    //trabajar con el state
    const [ clientes,guardarCLientes]=useState([]);
    //utilizar valor context
    const [auth, guardarAuth ] = useContext(Â CRMContext );
    //query a la api
    const consultarApi=async ()=>{
        const clientesConsulta=await clienteaAxios.get('/clientes');
        //coloca el resultado en el stae
        guardarCLientes(clientesConsulta.data);
        //console.log(clientesConsulta)

    }
    //use effect es similar a componentdidmount y willmount
    useEffect( ()=>{
        consultarApi();
    },[clientes]);


    // Si el state esta como false
      if(!auth.auth) {
        props.history.push('/iniciar-sesion');
    }

    if(!clientes.length) return <Spinner /> 

    return(
        <Fragment>
            <h2>Clientes</h2>

            <Link to={"/clientes/nuevo"} class="btn btn-verde nvo-cliente"> 
                <i class="fas fa-plus-circle"></i>
                Nuevo Cliente
            </Link>

            <ul class="listado-clientes">
                {clientes.map(cliente=>(
                   <Cliente 
                        key={cliente._id}
                        cliente={cliente}
                   />
                ))}
            </ul>
        </Fragment>
        
    )
}

export default Clientes;
